    <Square value={squares[2]}/>
  </div>
  <div className="board-row">
    <Square value={squares[3]}/>
    <Square value={squares[4]}/>
    <Square value={squares[5]}/>
  </div>
  <div className="board-row">
    <Square value={squares[6]}/>
